const boxes = document.querySelectorAll('.box');

boxes.forEach((box, idx) => {
  box.addEventListener('click', () => {
    if (box.classList.contains('unclick')) {
      box.addEventListener('mouseout', () => {
        box.classList.remove('unclick');
        box.classList.add('select');
      });
    } else if (box.classList.contains('select')) {
      box.addEventListener('mouseout', () => {
        box.classList.remove('select');
        box.classList.add('default');
      });
    } else if (box.classList.contains('default')) {
      box.addEventListener('mouseout', () => {
        box.classList.remove('default');
        box.classList.add('unclick');
      })
    } else {
      box.classList.remove('unclick')
    }

  });
});